import React, {Component} from 'react';
import {
  View,
  Text,
  Image,
  StyleSheet,
  FlatList,
  Dimensions,
} from 'react-native';

const styles = StyleSheet.create({
    list: {
        flex:1,
    },
    item: {
        height: Dimensions.get('window').height / 18,
        flexDirection: "row",
        borderWidth: 1,
        borderColor: "black"
    },
    item_img: {
        width: "15%",
        height: "100%",
    },
    item_name: {
        width: "45%",
        height: "100%",
        textAlignVertical: 'center',
        textAlign: 'center'
    },
    item_age: {
        width: "20%",
        height: "100%",
        textAlignVertical: 'center',
        textAlign: 'center'
    },
    item_sex: {
        width: "20%",
        height: "100%",
        textAlignVertical: 'center',
        textAlign: 'center'
    }
});

class List extends Component<{items: string[]}> {
    prepareItem = (item) => {
        return <View
            style={styles.item}
        >
            <Image
                style={styles.item_img}
                // TODO: change this pls
                source={{ uri: `data:image/jpeg;base64,` }} />
            <Text
                style={styles.item_name}
            >
                {item}
            </Text>
            <Text
                style={styles.item_age}
            >
                Age
            </Text>
            <Text
                style={styles.item_sex}
            >
                Sex
            </Text>
        </View>
    }

    render(): React.ReactNode {
        let no_items = false;
        if(this.props.items.length == 0) {
            no_items = true;
        }
        return (
            <View
            style={styles.list}
            >
                {no_items &&
                    <Text
                        style={{
                            flex: 1,
                            textAlign: "center",
                            textAlignVertical: 'center'
                        }}
                    >
                        No patients
                    </Text>
                }
                {!no_items &&
                    <FlatList
                        style={this.props.items.length == 0 ? { display: 'none' } : {}}
                        data={this.props.items}
                        renderItem={(item) => this.prepareItem(item.item)}
                    >
                    </FlatList>
                }
            </View>
        );
    }

}

export default List;